INSERT INTO parameter_datatype VALUES (0, 'INTEGER', 'any integer value in the range -4,000 trillion to +4,000 trillion', 'number');
INSERT INTO parameter_datatype VALUES (1, 'DOUBLE', 'Floating-point numbers with double precision.', 'number');
INSERT INTO parameter_datatype VALUES (2, 'NUMBER', 'any number type ( Integer or Double)', 'number');
INSERT INTO parameter_datatype VALUES (3, 'DATE', 'according to format definition in ISO 8601 xsd:dateTime (e.g. 2018-01-26T23:32:52+02:00 or 2018-01-26)', 'String');
INSERT INTO parameter_datatype VALUES (4, 'FILE', 'Represents only the file name. The file should be placed in the folder containing the metedata file of the model.', 'String');
INSERT INTO parameter_datatype VALUES (5, 'BOOLEAN', 'Strings of ''true'' Or ''false'' are accepted', 'String');
INSERT INTO parameter_datatype VALUES (6, 'VECTOROFNUMBERS', 'One dimensional array of numbers. Values should be written inside square [ ] brackets, seperated by comma and each value can contain any decimal number It also uses a dot (decimal point) to represent decimal fractions. null represents empty value. e.g. [1,2,3,null,5,12.5]', 'String');
INSERT INTO parameter_datatype VALUES (7, 'VECTOROFSTRINGS', 'One dimensional array of strings. Values should be written inside square [ ] brackets, seperated by comma and each value can contain any character and provided between two single quotes '' ''. null represents empty value e.g. ["A", "back", null, "car"]', 'String');
INSERT INTO parameter_datatype VALUES (8, 'MATRIXOFNUMBERS', 'Two dimensional array of numbers. Elements are of type Vector[number] separated by comma and enclosed inside square [ ] brackets All elements must have the same length. null represents empty value. e.g. [[1,10,5], [1,2.6,3.5], [1,2,3], [110,250,750.66]]', 'String');
INSERT INTO parameter_datatype VALUES (8, 'MATRIXOFSTRINGS', 'Two dimensional array of strings. Elements are of type Vector[string] separated by comma and enclosed inside square [ ] bracket. All elements must have the same length. null represents empty value.  e.g.  [["Air","vibration","c"],["A", "B", "C"],[ "Aaba",null, "Baca"],["cc","b","here"]]', 'String');
INSERT INTO parameter_datatype VALUES (8, 'OBJECT', 'Valid JSON Object JSON objects are surrounded by curly braces {}.written in key/value pairs.Keys must be strings, and values must be a valid data type Keys and values are separated by a colon. Each key/value pair is separated by a comma. { ''name'':''John'', ''age'':30, ''car'':null }', 'String');
INSERT INTO parameter_datatype VALUES (8, 'STRING', 'Free text or any other data object which will be presented as string', 'String');